import React from 'react';

class ReviewForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: ''};

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({value: event.target.value});
    }

    handleSubmit(event){
      event.preventDefault();

      const title = this.title.value;
      const name = this.name.value;
      var today = new Date();
      var date = ('0' + (today.getMonth()+1)).slice(-2) + '/' + ('0' + today.getDate()).slice(-2) + '/' + today.getFullYear().toString().substr(-2);    
      const rating = this.rating.value;
      const review = this.review.value;

      console.log(date)

      const info = {
        Title: title,
        User: name,
        Date: date,
        Stars: rating,
        Review: review
      };

      const data = this.state.data;

      data.push(info);

      this.setState({
        data: data
      });
    }

    render() {
      return (
        <form onSubmit={this.handleSubmit}>
            <div className="title">ADD REVIEW</div>
            <label>
                <p>Rating</p>
                <select ref={value => this.rating = value}>
                    <option value="1">One star</option>
                    <option value="2">Two stars</option>
                    <option value="3">Three stars</option>
                    <option value="4">Four stars</option>
                    <option value="5">Five stars</option>
                </select>
            </label>
            <label>
                <p>Your name</p>
                <input type="text" ref={input => this.name = input} />
            </label>
            <label>
                <p>Review title</p>
                <input type="text" ref={input => this.title = input} />
            </label>
            <label>
                <p>Write your review below</p>
                <input type="text" ref={input => this.review = input} />
            </label>
            <div>
                <button type="cancel" onClick={this.props.close}>CANCEL</button>
                <button type="submit" >SUBMIT</button>
                {/* <input type="submit" value="Submit"/> */}
            </div>
        </form>
      );
    }
  }

export default ReviewForm;
